from django.forms import ModelFormfrom django.core.exceptions import ValidationErrorclass ItemAdminForm(ModelForm):  def clean(self):     cleaned_data = super().clean()     quantity = cleaned_data.get('quantity')     sold = cleaned_data.get('sold')     if not sold:       return cleaned_data     if quantity < sold:       text = "sold can't be greater than quantity"       raise ValidationError(text)     return cleaned_data         