from django.forms import ModelFormfrom django.core.exceptions import ValidationError# custom form validation for the item modelclass ItemAdminForm(ModelForm):    """  ensure that sold is always less than or equal to quantify  """    def clean(self):     cleaned_data = super().clean()     quantity = cleaned_data.get('quantity')     sold = cleaned_data.get('sold')     if not sold or not quantity:       return cleaned_data     if quantity < sold:       text = "sold can't be greater than quantity"       raise ValidationError(text)     return cleaned_data          class SaleAdminForm(ModelForm):    """  ensure that input price is an integer  """    def clean_price(self):     data = self.cleaned_data["price"]     try:        x = int(data)        return data     except:        text= "price field can only be integer or decimal"        raise ValidationError(text)       